sql.where:

    and domain_employee_info.chinese_name like CONCAT(#{nameLike,jdbcType=VARCHAR},'%') 
    and domain_employee_info.person_number = #{personnelNo, jdbcType=VARCHAR} 
    and domain_employee_info.group_id = #{groupId,jdbcType=INTEGER} 
    and domain_org_structure.group_name = #{groupName,jdbcType=INTEGER} 
    and domain_employee_info.state = #{personnelStatus,jdbcType=BIT} 
    and domain_employee_info.create_time > #{createTime,jdbcType=TIMESTAMP} 
    and domain_employee_info.update_time > #{updateTime,jdbcType=TIMESTAMP} 
    and domain_org_structure.unit_id = #{unitId,jdbcType=INTEGER} 
    and domain_org_structure.unit_name = #{unitName,jdbcType=TIMESTAMP} 
    and domain_org_structure.department_id = #{departmentId,jdbcType=INTEGER} 
    and domain_org_structure.department_name = #{departmentName,jdbcType=TIMESTAMP} 

and domain_employee_info.group_id = domain_org_structure.id
and (basic2.value_type = 'Diploma' and domain_employee_info.diploma = basic2.value_no)
and (basic1.value_type = 'Duty' and domain_employee_info.duty = basic1.value_no)

mapper.select:
select

from domain_employee_info, domain_org_structure, domain_basic_info basic1, domain_basic_info basic2


    limit ${page} ,${size}
